create table users
(
    id         int unsigned auto_increment
        primary key,
    username   varchar(255)                                                    null,
    email      varchar(255)                                                    null,
    password   varchar(255)                                                    not null,
    created_at datetime                                                        null,
    updated_at datetime                                                        null,
    github     tinyint(1)   default 0                                          not null,
    user_state tinyint(1)   default 1                                          not null,
    avatar_url varchar(255) default '/public/assets/images/avatars/avatar.jpg' null,
    constraint email
        unique (email),
    constraint username
        unique (username)
)
    engine = InnoDB;

